package com.musala.dronemanagerservice.controller;

import com.musala.dronemanagerservice.model.dto.BatteryDto;
import com.musala.dronemanagerservice.model.dto.DroneDto;
import com.musala.dronemanagerservice.model.dto.MedicationDto;
import com.musala.dronemanagerservice.model.dto.RegisterDroneDto;
import com.musala.dronemanagerservice.service.DroneManagerService;
import io.swagger.v3.oas.annotations.Parameter;
import io.swagger.v3.oas.annotations.media.Content;
import io.swagger.v3.oas.annotations.media.ExampleObject;
import io.swagger.v3.oas.annotations.media.Schema;
import io.swagger.v3.oas.annotations.responses.ApiResponse;
import io.swagger.v3.oas.annotations.responses.ApiResponses;
import lombok.RequiredArgsConstructor;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.PutMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.ResponseStatus;
import org.springframework.web.bind.annotation.RestController;

import java.util.Set;

@RestController
@RequestMapping("/drone")
@RequiredArgsConstructor
public class DroneController {
    private final DroneManagerService service;

    @PostMapping
    @ResponseStatus(HttpStatus.CREATED)
    @io.swagger.v3.oas.annotations.parameters.RequestBody(required = true,
            description = "Drone to be registered",
            content =
            @Content(
                    mediaType = MediaType.APPLICATION_JSON_VALUE,
                    schema = @Schema(implementation = RegisterDroneDto.class),
                    examples = @ExampleObject(name = "registerDroneRequest", value = """
                            {
                              "serialNumber": "rvwofg345y8t42gwhqierhf2348",
                              "model": "Lightweight",
                              "weightLimit": 500
                            }
                            """)
            )
    )
    @ApiResponses(value = {
            @ApiResponse(responseCode = "201"),
            @ApiResponse(responseCode = "400"),
            @ApiResponse(responseCode = "500")
    })
    public void registerDrone(@RequestBody RegisterDroneDto drone) {
        service.registerDrone(drone);
    }

    @PutMapping("/load/{serialNumber}")
    @ResponseStatus(HttpStatus.CREATED)
    @io.swagger.v3.oas.annotations.parameters.RequestBody(required = true,
            description = "Load medications",
            content =
            @Content(
                    mediaType = MediaType.APPLICATION_JSON_VALUE,
                    schema = @Schema(implementation = RegisterDroneDto.class),
                    examples = @ExampleObject(name = "loadMedicationRequest", value = """
                            [
                              {
                                "name": "Vitamin C",
                                "weight": 40,
                                "code": "35235rrqes341",
                                "image": ""
                              }
                            ]""")))
    @ApiResponses(value = {
            @ApiResponse(responseCode = "200", content = @Content(mediaType = MediaType.APPLICATION_JSON_VALUE, examples = @ExampleObject(name = "LoadedDroneInfo", value = """
                    {
                       "serialNumber": "3524fadfqe142",
                       "model": "Lightweight",
                       "weightLimit": 500,
                       "batteryCapacity": 39.3,
                       "state": "IDLE",
                       "medications": [
                         {
                           "name": "Vitamin C",
                           "weight": 40,
                           "code": "35235rrqes341",
                           "image": ""
                         }
                       ]
                     }
                     """))),
            @ApiResponse(responseCode = "400"),
            @ApiResponse(responseCode = "404"),
            @ApiResponse(responseCode = "500")
    })
    public DroneDto loadDrone(@Parameter(description = "Serial number of the drone to load") @PathVariable String serialNumber,
                              @RequestBody Set<MedicationDto> medication) {
        return service.loadDrone(serialNumber, medication);
    }

    @GetMapping("/checkLoading/{serialNumber}")
    @ApiResponses(value = {
            @ApiResponse(responseCode = "200", content = @Content(mediaType = MediaType.APPLICATION_JSON_VALUE, examples = @ExampleObject(name = "LoadedDroneInfo", value = """
                    [
                      {
                        "name": "Vitamin C",
                        "weight": 40,
                        "code": "35235rrqes341",
                        "image": ""
                      }
                    ]"""))),
            @ApiResponse(responseCode = "404"),
    })
    public Set<MedicationDto> checkLoading(@Parameter(description = "Serial number of the drone to check loading") @PathVariable String serialNumber) {
        return service.checkLoading(serialNumber);
    }

    @GetMapping("/available")
    @ApiResponses(value = {
            @ApiResponse(responseCode = "200", content = @Content(mediaType = MediaType.APPLICATION_JSON_VALUE, examples = @ExampleObject(name = "LoadedDroneInfo", value = """
                    [{
                       "serialNumber": "3524fadfqe142",
                       "model": "Lightweight",
                       "weightLimit": 500,
                       "batteryCapacity": 39.3,
                       "state": "IDLE"
                     },
                     {
                       "serialNumber": "435234qewg",
                       "model": "Lightweight",
                       "weightLimit": 500,
                       "batteryCapacity": 70.3,
                       "state": "IDLE"
                     }]"""))),
            @ApiResponse(responseCode = "400"),
            @ApiResponse(responseCode = "500")
    })
    public Set<DroneDto> getAvailableDrones() {
        return service.getAvailableDrones();
    }

    @GetMapping("/battery/{serialNumber}")
    @ApiResponses(value = {
            @ApiResponse(responseCode = "200", content = @Content(mediaType = MediaType.APPLICATION_JSON_VALUE, examples = @ExampleObject(name = "LoadedDroneInfo", value = """
                    {
                      "serialNumber": "64c5083015dbb401e23373fc",
                      "batteryCapacity": 24.6
                    }"""))),
            @ApiResponse(responseCode = "404"),
            @ApiResponse(responseCode = "500")
    })
    public BatteryDto checkBattery(@Parameter(description = "Serial number of the drone to check battery") @PathVariable String serialNumber) {
        return service.checkBattery(serialNumber);
    }

}
